import React from "react";
import { useLocation } from "react-router-dom";
import "antd/dist/antd.css";
import { Breadcrumb, Image } from "antd";
import Typography from "@material-ui/core/Typography";
import Paragraph from "antd/lib/typography/Paragraph";

import "./ArticlePageContent.css";
import { Divider, Link } from "@material-ui/core";

function ArticlePageContent() {
  const location = useLocation();
  var headline = null;

  const params = new URLSearchParams(location.search);

  headline = {
    author: params.get("author") || "",
    content: params.get("content") || "",
    description: params.get("description") || "",
    publishedAt: new Date(params.get("publishedAt") || ""),
    source: {
      id: params.get("id") || "",
      name: params.get("name"),
    },
    title: params.get("title") || "",
    url: params.get("url") || "",
    urlToImage: params.get("urlToImage"),
  };

  const formatDate = (date: Date) => {
    return date.toUTCString();
  };

  console.log(headline);

  return (
    <>
      <Breadcrumb style={{ margin: "20px" }}>
        <Breadcrumb.Item>
          <Link href="/">Home</Link>
        </Breadcrumb.Item>
        <Breadcrumb.Item>Article</Breadcrumb.Item>
      </Breadcrumb>
      <div className="single_article">
        <Image
          src={
            headline.urlToImage !== "null" && headline.urlToImage != null
              ? headline.urlToImage
              : "error"
          }
          style={{ objectFit: "cover" }}
          width={"100%"}
          fallback="data:image/png;base64,"
        ></Image>
        <div className="single_article_content">
          <Typography variant="h5">
            {headline.title !== "null" ? headline.title : "No title available"}
          </Typography>
          <div className="single_article_description">
            <Paragraph>
              {headline.description !== "null"
                ? headline.description
                : `No description available`}
            </Paragraph>
          </div>
          <div className="single_article_divider">
            <Divider />
          </div>
          <div className="single_article_details">
            <Typography variant="subtitle2">
              Source:{" "}
              {headline.source.name !== "null"
                ? headline.source.name
                : "No source available"}
            </Typography>
            <Typography variant="subtitle2">
              Author:{" "}
              {headline.author !== "null"
                ? headline.author
                : "No author available"}
            </Typography>
            <Typography variant="subtitle2">
              Published at:{" "}
              {headline.publishedAt != null
                ? formatDate(headline.publishedAt)
                : "No publish date available"}
            </Typography>
            {headline.url !== "null" ? (
              <Typography variant="subtitle2">
                More about the Article on the following source:{" "}
                <Link href={headline.url}>link</Link>
              </Typography>
            ) : (
              "No source link available"
            )}
            <div className="single_article_divider">
              <Divider />
            </div>
            <div className="single_article_full_content">
              <Typography variant="subtitle2" style={{ paddingTop: "10px" }}>
                {headline.content !== "null"
                  ? headline.content.replaceAll(/\[\s\d+\schars\]/g, "")
                  : `No content available`}
                {headline.url !== "null" ? (
                  <Link href={headline.url}>more on the following link</Link>
                ) : (
                  ""
                )}
              </Typography>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default ArticlePageContent;
